#title: Totala antalet same-as länkar till Wikidata

PREFIX owl: <http://www.w3.org/2002/07/owl#>

SELECT (COUNT(?external_id) AS ?wikidata_count) WHERE {
  [] owl:sameAs ?external_id .

  FILTER(CONTAINS(STR(?external_id), "http://www.wikidata.org/entity/")) 
} LIMIT 200

#title: Totala antalet same-as länkar

PREFIX owl: <http://www.w3.org/2002/07/owl#>

SELECT (COUNT(?external_id) AS ?wikidata_count) WHERE {
  [] owl:sameAs ?external_id .
} LIMIT 200

#defaultView:Map
#title: Karta med byggår för Kepler.gl

PREFIX schema: <http://schema.org/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX knav-property: <http://kulturnav.org/schema/property/>
PREFIX time: <http://www.w3.org/2006/time#>

SELECT ?geometryTitle ?lat ?lon ?constructionTimeStart ?constructionTimeEnd WHERE {
  ?thing a owl:Thing ;
         knav-property:localizedObject.geometry ?geometry .
  ?geometry schema:longitude ?lon ;
            schema:latitude ?lat .
  ?thing schema:name ?geometryTitle ;
         knav-property:construction.time ?constructionTime .
  ?constructionTime time:hasBeginning ?constructionStart ;
                    time:hasEnd ?constructionEnd .

  # Vi formaterar åren som datum så att Kepler.gl känner igen datatypen
  BIND(REPLACE(STR(?constructionStart), 'Z', '-01-01 00:00:00') AS ?constructionTimeStart)
  BIND(REPLACE(STR(?constructionEnd), 'Z', '-01-01 00:00:00') AS ?constructionTimeEnd)
} LIMIT 100

#defaultView:Map
#title: Karta med hur många år det tog att bygga byggnaden för Kepler.gl

PREFIX schema: <http://schema.org/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX knav-property: <http://kulturnav.org/schema/property/>
PREFIX time: <http://www.w3.org/2006/time#>

SELECT ?geometryTitle ?lat ?lon ?constructionTimeStart ?normalizedTimespan WHERE {
  ?thing a owl:Thing ;
         knav-property:localizedObject.geometry ?geometry .
  ?geometry schema:longitude ?lon ;
            schema:latitude ?lat .
  ?thing schema:name ?geometryTitle ;
         knav-property:construction.time ?constructionTime .
  ?constructionTime time:hasBeginning ?constructionStart ;
                    time:hasEnd ?constructionEnd .

  BIND(YEAR(?constructionEnd) - YEAR(?constructionStart) AS ?constructionTimespan)
  BIND(IF(?constructionTimespan = 0, 1, ?constructionTimespan) AS ?normalizedTimespan)

} LIMIT 100

#title: Byggnader som Erik Dahlbergh varit arkitekt för

PREFIX schema: <http://schema.org/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX knav-property: <http://kulturnav.org/schema/property/>
PREFIX time: <http://www.w3.org/2006/time#>

SELECT ?thing ?name WHERE {  
  ?thing a owl:Thing ;
         schema:name ?name ;
         knav-property:building.agent ?agentNode .

  ?agentNode knav-property:relation.agentRole <http://kulturnav.org/eb04f87d-71e1-49ef-8873-44c8265746ac> ; # Arkitekt
             knav-property:namedObject.agent.agent <http://kulturnav.org/ec717f5b-e17e-40ae-ae2b-5edd9a1c2a55> . # Erik Dahlbergh
}
