PREFIX schema: <http://schema.org/>

SELECT ?creator (COUNT(?site) AS ?site_count) WHERE {
  BIND(<https://fornpunkt.se/tagg/stensattning> AS ?tag)
  ?site schema:keywords ?tag ;
        a schema:CreativeWork ;
        schema:creator ?creator .
}
GROUP BY ?creator
ORDER BY DESC(?site_count)

PREFIX schema: <http://schema.org/>
SELECT * WHERE {
  ?site a schema:CreativeWork ;
       schema:name ?name .

  FILTER(CONTAINS(?name, '?'))
}

PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?other_tags ?name WHERE {
  BIND(<https://fornpunkt.se/tagg/stensattning> AS ?tag)
  ?site schema:keywords ?tag ;
        schema:keywords ?other_tags .
  ?other_tags schema:name ?name .

  MINUS {
    ?tag schema:name ?origin_tag_name .
    ?other_tags schema:name ?origin_tag_name .
  }
}

PREFIX schema: <http://schema.org/>
SELECT * WHERE {
  ?tag a schema:DefinedTerm ;
       schema:name ?name .

  OPTIONAL { ?tag schema:description ?description }

  MINUS { ?tag schema:subjectOf [] }
}

PREFIX schema: <http://schema.org/>

SELECT ?creator (COUNT(?site) AS ?site_count) WHERE {
  ?site a schema:CreativeWork ;
        schema:creator ?creator .
}
GROUP BY ?creator
ORDER BY DESC(?site_count)

PREFIX schema: <http://schema.org/>
SELECT ?observation_type (COUNT(?observation_type) AS ?observation_type_count) WHERE {
  BIND('abbe'@sv AS ?user)
  ?site schema:creator ?user ;
        schema:event ?event .
  ?event a ?observation_type .
}
GROUP BY ?observation_type
ORDER BY DESC(?observation_type_count)

PREFIX schema: <http://schema.org/>
SELECT * WHERE {
  ?site a schema:CreativeWork ;
        schema:name 'Stensättning'@sv .
}

PREFIX schema: <http://schema.org/>
SELECT ?tag ?wikidata WHERE {
  ?tag a schema:DefinedTerm ;
       schema:subjectOf ?wikipedia .

  SERVICE <https://query.wikidata.org/sparql> {
    ?wikipedia schema:about ?wikidata .
  }
}

PREFIX schema: <http://schema.org/>
SELECT * WHERE {
  ?site a schema:CreativeWork ;
        schema:creator 'abbe'@sv ;
        schema:name ?site_title .
}

PREFIX schema: <http://schema.org/>
SELECT * WHERE {
  ?tag a schema:DefinedTerm ;
       schema:name ?name .

  OPTIONAL { ?tag schema:subjectOf ?wikipedia }

  MINUS { ?tag schema:description [] }
}
